angular.module("psd",[]).controller("psdCtrl",["$scope","$http","$window",function(n,o,t){n.uname=t.name.split("&")[0],n.change=function(){console.log(n.oldPsd),console.log(n.newPsd),o({url:"http://127.0.0.1:9090/api/updatapwd",method:"get",params:{account:n.uname,password:n.oldPsd,updatapwd:n.newPsd}}).then(function(n){"true"==n.data&&(alert("修改成功"),t.name="",t.location.href="index.html")}).catch(function(n){console.log(n)})}}]);